sources.styles/_variables.scss:
@import url('https://fonts.googleapis.com/css2?family=Anton&family=Noto+Sans+JP:wght@400;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Dosis:wght@400;700&family=Metal+Mania&display=swap');

$color-main: #FEE903;
$color-text: #000;
$color-reverse: #fff;
$color-shop: #eaeaea;
$color-shop-active: #ff40b4;

$font-heading: 'Anton', sans-serif;
$font-text: 'Noto Sans JP', sans-serif;
$font-shop-heading: 'Dosis', sans-serif;
$font-important-title: 'Metal Mania', cursive;

sources.styles/_animations.scss:
@keyframes anim-main-swell {
    from {
        transform: translate(-50%, -50%) scale(1);
    }

    to {
        transform: translate(-50%, -50%) scale(1.2);
    }
}

sources.styles/_reset.scss:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

*:active {
    outline: none;
}

a {
    text-decoration: none;
}

ul, ul li {
    list-style: none;
}

sources.styles/_main.scss:
body { 
    background-color: $color-main;
    font-family: $font-text;
    font-weight: 400;
    font-size: 62.5%;
    letter-spacing: .8px;
}

.section {
    height: 100vh;
    width: 100vw;
    position: relative;
}

.section#section-main {
    background-color: $color-main;
    height: calc(100vh + 15vh);

    img {
        width: 55%;
        position: fixed;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);

        animation-name: anim-main-swell;
        animation-duration: 2500ms;
        animation-delay: 400ms;
        animation-iteration-count: infinite;
        animation-direction: alternate;
    }

    .section-main-reward {
        width: 100%;
        text-align: center;
        position: fixed;
        top: 2.8rem;
        opacity: 0;
        transition: opacity 1000ms ease-in;

        .section-main-reward--quote {
            font-size: .7rem;
            font-weight: 700;
            font-style: italic;
            margin-bottom: .3rem;
        }

        &.active {
            opacity: 1;
        }
    }
}

.section#section-tagline {
    height: 120vh;
    background-color: $color-text;
    clip-path: polygon(0 0, 100% 15%, 100% 100%, 0 100%);
    padding: 2.5rem;
    display: flex;
    justify-content: center;
    align-items: center;

    h1 {
        font-family: $font-heading;
        text-transform: uppercase;
        color: $color-main;
        font-size: 6.2rem;
        line-height: 6rem;
        padding-top: 10rem;
        letter-spacing: 4px;
    }
}

.section#section-rewards {
    background-color: $color-main;
    padding: 4rem 1.5rem;

    h1 {
        width: 100%;
        text-align: center;
        font-family: $font-heading;
        font-size: 2rem;
        text-transform: uppercase;
    }

    ul {
        width: 100%;
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        align-items: center;
        margin-top: 2.5rem;

        li {
            width: 23%;
            text-align: center;
            transition: transform 300ms ease-in;
            cursor: pointer;

            h2 {
                font-family: $font-important-title;
                font-size: 1.2rem;
            }

            svg {
                width: 90px;
                height: 90px;
            }

            &:hover { 
                transform: scale(1.2);
            }

            &:last-child svg {
                transform: rotate(-40deg);
            }
        }
    }
}

.section.section-sm {
    height: initial;
}


@media screen and (min-width: 1100px) {
    .section#section-main {
        img {
            width: 40%;
        }
    }

    .section#section-tagline {
        h1 {
            font-size: 7.5rem;
            line-height: 7rem;
        }
    }
}

@media screen and (min-width: 1350px) {
    .section#section-tagline {
        h1 {
            font-size: 9rem;
            line-height: 8.5rem;
        }
    }

    .section#section-rewards {
        h1 {
            font-size: 4rem;
        }

        ul {
            width: 80%;
            margin: 4rem auto 2rem;

            li {
                svg {
                    width: 120px;
                    height: 120px;
                }
            }
        }
    }
}


@media screen and (max-width: 750px) {
    .section#section-tagline {
        h1 {
            font-size: 5.5rem;
            line-height: 5.4rem;
        }
    }
}

@media screen and (max-width: 580px) {
    .section#section-tagline {
        h1 {
            font-size: 4.5rem;
            line-height: 5rem;
        }
    }
}

sources.styles/_shop.scss:
.shop-section {
    width: 100%;
    overflow-x: hidden;

    &#section-shop--header {
        height: 60vh;
        background-position: center center;
        background-size: cover;
    }

    &#section-shop--title {
        background-color: $color-reverse;
        text-align: center;
        font-family: $font-shop-heading;
        padding: 6rem;

        h1 {
            font-size: 3rem;
            font-weight: 700;
        }

        h2 {
            font-size: 1.8rem;
            font-weight: 400;
            padding-top: .4rem;
            line-height: 1.8rem;
        }

        p {
            text-align: left;
            padding-top: 2rem;
            font-family: $font-text;
            font-size: 1rem;
            letter-spacing: .4px;
        }
    }

    &#section-shop--items {
        background-color: $color-shop;

        h1 {
            text-align: center;
            font-family: $font-text;
            font-size: 1.45rem;
            padding: 1rem 0;
            letter-spacing: 14px;
            border-bottom: 1px solid black;
        }

        .section-shop-items--container {

            &--item {
                padding: 2rem 2rem;
                margin: 0 2.5rem;
                border-bottom: 1px solid black;

                display: flex;
                justify-content: center;
                align-items: center;

                &--description {
                    width: 30%;

                    h2 {
                        margin-bottom: .5rem;
                        font-family: $font-important-title;
                        font-size: 1.5rem;
                    }

                    p {
                        margin-bottom: 1rem;
                    }

                    button {
                        border: 1px solid black;
                        background-color: $color-reverse;
                        padding: .3rem .6rem;
                        font-family: $font-text;
                        font-size: .6rem;
                        cursor: pointer;
                        transition: all 200ms ease-in;

                        &:hover {
                            background-color: $color-shop-active;
                            border-color: $color-shop-active;
                        }
                    }
                }

                &--image {
                    padding-left: 3rem;
                    width: 70%;
                    cursor: pointer;

                    img {
                        width: 100%;
                        transition: transform 200ms ease-out;

                        &:hover {
                            transform: scale(1.1);
                        }
                    }
                }

                &:last-child {
                    border: none;
                }
            }
        }
    }
}

@media screen and (min-width: 1300px) {
    .shop-section#section-shop--header {
        height: 75vh;
    }

    .shop-section#section-shop--title {
        padding: 9rem 12rem;

        h2 {
            padding-top: .6rem;
        }

        p {
            padding-top: 4rem;
        }
    }

    .shop-section#section-shop--items {
        background-color: $color-shop;

        h1 {
            text-align: center;
            font-family: $font-text;
            font-size: 1.45rem;
            padding: 1rem 0;
            letter-spacing: 14px;
            border-bottom: 1px solid black;
        }

        .section-shop-items--container {

            &--item {
                margin: 0 7rem;

                &--description {
                    width: 45%;

                    h2 {
                        margin-bottom: .7rem;
                        font-size: 1.6rem;
                    }

                    p {
                        font-size: 1rem;
                        margin-bottom: 1.2rem;
                    }

                    button {
                        padding: .5rem .8rem;
                        font-size: .8rem;
                    }
                }

                &--image {
                    padding-left: 6rem;
                    width: 55%;
                }
            }
        }
    }
}

@media screen and (max-width: 850px) {
    .shop-section#section-shop--header {
        height: 40vh;
    }
}

@media screen and (max-width: 650px) {
    .shop-section#section-shop--title {
        padding: 2.65rem;
    }

    .shop-section#section-shop--items {

        h1 {
            font-size: 1.10rem;
            letter-spacing: 12px;
        }

        .section-shop-items--container {
            &--item {
                padding: 2rem 1rem;
                margin: 0 1.5rem;
                display: flex;
                flex-direction: column;
                justify-content: center;
                align-items: center;

                &--description {
                    width: 100%;
                    margin-bottom: 2rem;

                    button {
                        margin-top: .5rem;
                    }
                }

                &--image {
                    margin: 0;
                    padding: 0;
                    width: 100%;
                }
            }
        }
    }
}
